
public class MultiplicationMethod {
	public void run(int j) {
		for(int i=1;i<=10;i++) {
			System.out.println(j+" X "+i+" = "+(i*j));
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		MultiplicationMethod ob=new MultiplicationMethod();
		ob.run(12);
		
	}

}
